import { useStorage } from '@capacitor-community/react-hooks/storage';

let tableLoca: any[] = [];

export function useToDoList() {


    const { get, set, remove } = useStorage();

    //Pour initialiser la liste stockées en local et la stocker sur la liste temporaire
    const initializeItems2d = () => { get('todos2d').then((valueInitial2d) => {
        if (valueInitial2d != null) {        
            tableLoca = JSON.parse(valueInitial2d)  
        } else {
            tableLoca = ['vide']
        }
    })}

    //Pour ajouter des items et les sauvegarder en local : 
    const addItem = (itemValue: string) => {

        if(itemValue === undefined ) {
            return
        }

        if (tableLoca[0] === 'vide') {
            tableLoca = []
        }

        // je fais le tableau en 2 dimensions afin de stocker l'état
        tableLoca.push([itemValue, 'active'])
        remove('todos2d')
        set('todos2d', JSON.stringify(tableLoca))
    }

    //Pour changer l'état d'une entrée
    const changeState = (key: any) => {
        const tableContent = tableLoca[key.index]
        if (tableContent[1] === 'active') {
            tableContent[1] = 'done'
        } else if (tableContent[1] === 'done') {
            tableContent[1] = 'active'
        }

        //On actualise les données
        tableLoca[key.index] = tableContent
        remove('todos2d')
        set('todos2d', JSON.stringify(tableLoca))
    } 

    //Pour retirer tous les items stockés dans la liste : 
    const removeAllItems = () => {

        remove('todos2d')
        tableLoca = ['vide']
    }    

    return {
        addItem, removeAllItems, tableLoca, initializeItems2d, changeState
    }

}
