import { useStorage } from '@capacitor-community/react-hooks/storage';
import { verify } from 'crypto';
import { useState } from 'react';

let counter = 0

export function useToDoList() {

    let item = {
        id: 1,
        text: 'un exemple',
        checked: false
    }


    let [listItems, updateList] = useState([item]);

    function addItem(text) {
        const newItem = {
            id: counter++,
            text: text,
            checked: false
        }

        listItems.concat(newItem)

        updateList(newItem)
    }


    return {
        listItems, addItem
    }

}
