import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonInput, IonButton, IonCheckbox, IonIcon } from '@ionic/react';
import React from 'react';
import './Home.css';
import { useToDoList } from '../hooks/useToDoList';
import { useState } from "react";
import { create } from 'ionicons/icons';

const Home: React.FC = () => {

  const { addItem, removeAllItems, tableLocal, initializeItems2d, changeState } = useToDoList();
  const [text, setText] = useState<any>();
  initializeItems2d();

  const testState = (valeur: string) => {
    if (valeur === 'active') { return 'unlined' }
    else if (valeur === 'done') { return 'lined' }
  }

  const isChecked = (valeurC: string) => {
    if (valeurC === 'active') { return false }
    else if (valeurC === 'done') { return true }
  }

  const changeStateCheckbox = (event : boolean, valueTo : any) => {
    console.log(event);

    if (event === true) {
      <IonLabel className={'lined'}>{valueTo}</IonLabel>
    } else if (event === false) {
      <IonLabel className={'unlined'}>{valueTo}</IonLabel>
    }

   }

  const displayItems = () => {
    if (tableLocal[0] === "vide") {
      return <IonItem><IonLabel>Félicitation votre liste est vide</IonLabel></IonItem>
    }



    else {
      return tableLocal.map((value, index) => (
        <IonItem key={index}>
          <IonCheckbox slot="start" color="primary" checked={isChecked(value[1])} onIonChange={e => { changeState({ index })
        changeStateCheckbox(e.detail.checked, value[0])
        }} />

          <IonIcon icon={create} color="primary" slot="end" />
        </IonItem>
      ))
    }
  }

  //utiliser un setState ou le state checked


  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>To do list</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>

        <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large">To do list</IonTitle>
          </IonToolbar>
        </IonHeader>


        <IonList>
          {
            displayItems()
          }
        </IonList>


        <IonItem>
          <IonButton color="primary" href='' type="submit" onClick={() => removeAllItems()}>Vider totalement la liste</IonButton>
        </IonItem>



        <IonItem>
          <IonInput placeholder="Qu'avez-vous en tête ?" clearInput autofocus required onIonChange={e => { setText(e.detail.value); }}></IonInput>
          <IonButton color="primary" href='' type="submit" onClick={() => { addItem(text) }}>Créer</IonButton>
        </IonItem>



      </IonContent>


    </IonPage>
  );
};



export default Home;
