//import { useState, useEffect } from "react";

import { Plugins } from '@capacitor/core';
//import { IonInput } from '@ionic/react';

const { Storage } = Plugins;


const TODO_STORAGE = "todos";
const tableauToDo: string[] = [];

export function useToDoList() {

    const createItem = (itemValue: string) => {
        console.log('Le texte envoyé :', itemValue)
        tableauToDo.push(itemValue)
        console.log(tableauToDo)

        tableauToDo.map((value, index) => Storage.set({
            key: 'todos',
            value: JSON.stringify({
                id: index,
                name: value
            })
        }))
    }

    const getItems = () => {
        const ret = Storage.get({ key: 'tod' });
        const user = JSON.parse(ret.value);
    }

    return {
        createItem, tableauToDo, getItems
    }

}


export interface ToDoList {
    filepath: string;
    webviewPath?: string;
}
