import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonInput, IonButton, IonToggle, IonRadio, IonCheckbox, IonItemSliding, IonItemOption, IonItemOptions, IonIcon, IonFooter } from '@ionic/react';
import React from 'react';
//import ExploreContainer from '../components/ExploreContainer';
import './Home.css';
import { useToDoList } from '../hooks/useToDoList';
import { useState, SetStateAction } from "react";
import { create } from 'ionicons/icons';
import { getClassName } from '@ionic/react/dist/types/components/utils';
import { forceUpdate } from 'ionicons/dist/types/stencil-public-runtime';

const Home: React.FC = () => {

  const { tableauToDo, addItem, initializeItems, removeAllItems, tableauNew, initializeItems2d, changeState } = useToDoList();
  const [text, setText] = useState<any>();
  initializeItems();
  initializeItems2d();

  const testState = (valeur: string) => {
    if (valeur == 'active') { return 'unlined' }
    else if (valeur == 'done') { return 'lined' }
  }

  const isChecked = (valeurC: string) => {
    if (valeurC == 'active') { return false }
    else if (valeurC == 'done') { return true }
  }

  const displayItems = tableauNew.map((value, index) => (
    <IonItem key={index} onIonChanhan={e => {console.log(e.detail)}}>
      <IonCheckbox slot="start" color="primary" checked={isChecked(value[1])} onIonChange={() => { changeState({ index }) }} />
      <IonLabel className={testState(value[1])}>{value[0]}</IonLabel>
      <IonIcon icon={create} color="primary" slot="end" />
    </IonItem>
  ))

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>To do list</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>

        <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large">To do list</IonTitle>
          </IonToolbar>
        </IonHeader>


        <IonList>
          {
            displayItems
          }
        </IonList>


        <IonItem>
          <IonButton color="primary" href='' type="submit" onClick={() => removeAllItems()}>Vider totalement la liste</IonButton>
        </IonItem>


        <IonFooter>
          <IonItem>
            <IonInput placeholder="Qu'avez-vous en tête ?" clearInput autofocus required onIonChange={e => { setText(e.detail.value); }}></IonInput>
            <IonButton color="primary" href='' type="submit" onClick={() => { addItem(text) }}>Créer</IonButton>
          </IonItem>
        </IonFooter>


      </IonContent>


    </IonPage>
  );
};



export default Home;
