import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonInput, IonButton, IonToggle, IonRadio, IonCheckbox, IonItemSliding, IonItemOption, IonItemOptions, IonIcon } from '@ionic/react';
import React from 'react';
//import ExploreContainer from '../components/ExploreContainer';
import './Home.css';
import { useToDoList } from '../hooks/useToDoList';
import { useState } from "react";
import { create } from 'ionicons/icons';
import { getClassName } from '@ionic/react/dist/types/components/utils';

const Home: React.FC = () => {

  const { tableauToDo, addItem, initializeItems, removeAllItems } = useToDoList();
  const [text, setText] = useState<any>();
  initializeItems();

  const [isChecked, setChecked] = useState(false);



  if (isChecked == true) {
    console.log('its true')
    isLined = 'lined';
    return isLined
  }

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>To do list</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large">To do list</IonTitle>
          </IonToolbar>
        </IonHeader>


        <IonList>
          {
            tableauToDo.map((value, index) => (
              <IonItem key={index}>
                <IonCheckbox slot="start" color="primary" onIonChange={e => {setChecked(e.detail.checked!)} }/>
                <IonLabel>{value}</IonLabel>
                <IonIcon icon={create} color="primary" slot="end" />
              </IonItem>
            ))
          }
        </IonList>


        <IonItem>
          <IonButton color="primary" type="submit" onClick={() => removeAllItems() }>Vider totalement la liste</IonButton>
        </IonItem>

        <IonItem>
          <IonInput placeholder="Qu'avez-vous en tête ?" clearInput autofocus required onIonChange={e => { setText(e.detail.value!); }}></IonInput>
          <IonButton color="primary" type="submit" onClick={() => { addItem(text) }}>Créer</IonButton>
        </IonItem>

      </IonContent>
    </IonPage>
  );
};



export default Home;
