import { useState, useEffect, useCallback } from "react";
import { isPlatform } from '@ionic/react';
import { Plugins } from '@capacitor/core';
import { useStorage, useStorageItem, availableFeatures } from '@capacitor-community/react-hooks/storage';


let tableauToDo: string[] = [];
let tableauNew: any[] = [];

export function useToDoList() {

    const { get, set, remove, getKeys, clear } = useStorage();

    //Pour initialiser la liste stockées en local et la stocker sur la liste temporaire
    const initializeItems = () => { get('todos').then((valueInitial) => {
        if (valueInitial != null) {
            const tableauActual = JSON.parse(valueInitial)           
            tableauToDo = tableauActual
        }
    })}

    //Pour ajouter des items et les sauvegarder en local : 
    const addItem = (itemValue: string) => {
        tableauToDo.push(itemValue)
        remove('todos')
        set('todos', JSON.stringify(tableauToDo))

        // je fais le tableau en 2 dimensions afin de stocker 
        tableauNew.push([itemValue, 'active'])
    }

    //Pour retirer tous les items stockés dans la liste : 
    const removeAllItems = () => {
        remove('todos')
        tableauToDo = []
    }

    //tableauNew = [
    //     [ 'a',
    //     'done'], ['b', 'active']
    // ]

    // console.log(JSON.stringify(tableauNew));

    // console.log(JSON.parse(JSON.stringify(tableauNew)))



    return {
        addItem, tableauToDo, removeAllItems, initializeItems
    }

}
