import { useState, useEffect, useCallback } from "react";
import { isPlatform } from '@ionic/react';
import { Plugins } from '@capacitor/core';
import { useStorage, useStorageItem, availableFeatures } from '@capacitor-community/react-hooks/storage';
import { forceUpdate } from "ionicons/dist/types/stencil-public-runtime";


let tableauToDo: string[] = [];
let tableauNew: any[] = [];

export function useToDoList() {
    tableauNew = ['vide'];

    const { get, set, remove, getKeys, clear } = useStorage();

    //Pour initialiser la liste stockées en local et la stocker sur la liste temporaire
    const initializeItems = () => { get('todos').then((valueInitial) => {
        if (valueInitial != null) {     
            tableauToDo = JSON.parse(valueInitial)   
        }
    })}

    const initializeItems2d = () => { get('todos2d').then((valueInitial2d) => {
        if (valueInitial2d != null) {        
            tableauNew = JSON.parse(valueInitial2d)  
        }
    })}

    //Pour ajouter des items et les sauvegarder en local : 
    const addItem = (itemValue: string) => {

        if(itemValue == null ) {
            return
        }
        tableauToDo.push(itemValue)
        remove('todos')
        set('todos', JSON.stringify(tableauToDo))

        if (tableauNew == ['vide']) {
            tableauNew = []
        }

        // je fais le tableau en 2 dimensions afin de stocker l'état
        tableauNew.push([itemValue, 'active'])
        remove('todos2d')
        set('todos2d', JSON.stringify(tableauNew))
    }

    //Pour changer l'état d'une entrée
    const changeState = (key: any) => {
        const tableContent = tableauNew[key.index]
        if (tableContent[1] == 'active') {
            tableContent[1] = 'done'
        } else if (tableContent[1] == 'done') {
            tableContent[1] = 'active'
        }

        //On actualise les données
        tableauNew[key.index] = tableContent
        remove('todos2d')
        set('todos2d', JSON.stringify(tableauNew))
    } 

    //Pour retirer tous les items stockés dans la liste : 
    const removeAllItems = () => {
        remove('todos')
        tableauToDo = []

        remove('todos2d')
        tableauNew = ['vide']
    }    

    return {
        addItem, tableauToDo, removeAllItems, initializeItems, tableauNew, initializeItems2d, changeState
    }

}
