import { useState, useEffect, useCallback } from "react";
import { isPlatform } from '@ionic/react';
import { Plugins } from '@capacitor/core';
//import { IonInput } from '@ionic/react';
import { useStorage, useStorageItem, availableFeatures } from '@capacitor-community/react-hooks/storage';

//const { Storage } = Plugins;



const TODO_STORAGE = "todos";
const tableauToDo: string[] = [];


export function useToDoList() {

    const { get, set, remove, getKeys, clear } = useStorage();
    useEffect(() => {
        async function example() {
            const value = await get('name');
            await set('name', 'Maxime');
            await remove('name');
            const allKeys = await getKeys();
            await clear();
        }
    }, [get, set, remove, getKeys, clear]);

    const [name, setName] = useStorageItem('name', 'Maximum');

    // Example:
    const updateName = useCallback((n) => {
        setName(n);
    }, [setName]);


    //Exemple de set
    //set('name', 'Justin')
    //console.log(get('name'))

    const createItem = (itemValue: string) => {
        console.log('Le texte envoyé :', itemValue)
        tableauToDo.push(itemValue)
        console.log(tableauToDo)

        //Ne prends que la dernière entrée en compte
        //tableauToDo.map((value, index) => set('todos', JSON.stringify({ id: index, name: value})))
        //console.log(get('todos'))



        //Prends en compte plusieurs entrées mais écrase toujours les données précédentes
        set('todosSet', JSON.stringify(tableauToDo));

        console.log(get('todosSet'))





        const returnList = get('todoKepp').then((valueToDo) => {
            console.log('ici cest la bonne', valueToDo);

            if (valueToDo != null) {
                const valueToreturn = JSON.parse(valueToDo)
                set('todoKepp', JSON.stringify(valueToreturn.concat(tableauToDo)))
            } else {
                set('todoKepp', JSON.stringify(tableauToDo))
            }
            // expected output: "foo"
            const consoletest = async () =>console.log('Ici mtn', get('todoKepp'))

        })





        set('todoKepp', JSON.stringify(tableauToDo))


    }






    return {
        createItem, tableauToDo
    }

}


export interface ToDoList {
    filepath: string;
    webviewPath?: string;
}
