import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonList, IonItem, IonLabel, IonInput, IonButton, IonToggle, IonRadio, IonCheckbox, IonItemSliding, IonItemOption, IonItemOptions } from '@ionic/react';
import React from 'react';
//import ExploreContainer from '../components/ExploreContainer';
import './Home.css';
import { useToDoList, ToDoList } from '../hooks/useToDoList';
import { useState } from "react";

const Home: React.FC = () => {

  const { tableauToDo, saveItem, initializeItems, removeAllItems } = useToDoList();
  const [text, setText] = useState<any>();
  initializeItems();

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>To do list</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen>
        <IonHeader collapse="condense">
          <IonToolbar>
            <IonTitle size="large">To do list</IonTitle>
          </IonToolbar>
        </IonHeader>


        <IonList>
          {
            tableauToDo.map((value, index) => (
              <IonItem key={index}>
                <IonCheckbox slot="start" color="primary" />
                <IonLabel >{value}</IonLabel>
              </IonItem>
            ))
          }
        </IonList>


        <IonItem>
          <IonButton color="primary" type="submit" onClick={() => { removeAllItems() 
            initializeItems() }}>Vider totalement la liste</IonButton>
        </IonItem>

        <IonItem>
          <IonInput placeholder="Qu'avez-vous en tête ?" clearInput autofocus required onIonChange={e => { setText(e.detail.value!); }}></IonInput>
          <IonButton color="primary" type="submit" onClick={() => { saveItem(text) }}>Créer</IonButton>
        </IonItem>

      </IonContent>
    </IonPage>
  );
};

export default Home;
